validate.custom:
var d = new Date(input); var day = d.getDay(); var hour = d.getHours();
// Allowed days: Monday (1), Thursday (4), Friday (5), Saturday (6)
if ((day === 1 || day === 4 || day === 5 || day === 6) && (hour >= 10 && hour < 19)) { 
  valid = true; 
} else { 
  valid = 'Please choose a date on Monday, Thursday, Friday, or Saturday between 10 AM and 7 PM.'; 
}
